mapper.update:
update reply
set topicid = #{topicid,jdbcType=INTEGER},
  body = #{body,jdbcType=VARCHAR},
  userid = #{userid,jdbcType=INTEGER},
  create_on = #{createOn,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, topicid, body, userid, create_on
from reply
where id = #{id,jdbcType=INTEGER}
